    <a-input size="large" v-model:value="room_hash"/>
    <a-button type="dashed" size="large" @click="currentStep = 1">Назад</a-button>
    <a-button type="primary" size="large" @click="connect">Войти</a-button>
  </template>
</template>
<script lang="ts" setup>
import { ref, watch } from 'vue'
import { useRouter } from 'vue-router'

const $router = useRouter()

const room_hash = ref(localStorage.getItem('room_hash') || '')
const body_color = ref(localStorage.getItem('body_color') || '#ff0000')
const pants_color = ref(localStorage.getItem('pants_color') || '#0000ff')
const skin_color = ref(localStorage.getItem('skin_color') || '#ffff00')
const currentStep = ref<number>(0);
const steps = [
  {key: 'step1', title: 'Настройка персонажа'},
  {key: 'step2', title: 'Создать комнаты'},
  {key: 'step3', title: 'Подключиться к комнате'}
]

watch([body_color, pants_color, skin_color], () => {
  localStorage.setItem('body_color', body_color.value)
  localStorage.setItem('pants_color', pants_color.value)
  localStorage.setItem('skin_color', skin_color.value)
})
const connect = () => {
  if(room_hash.value.length === 0 )
    return alert('Укажите комнату')
  localStorage.setItem('room_hash', room_hash.value)
  localStorage.setItem('body_color', body_color.value)
  localStorage.setItem('pants_color', pants_color.value)
  localStorage.setItem('skin_color', skin_color.value)
  $router.push({ path: `/rooms/${room_hash.value}` })
}
const create = () => {
  room_hash.value = ''
  localStorage.removeItem('room_hash')
  localStorage.setItem('body_color', body_color.value)
  localStorage.setItem('pants_color', pants_color.value)
  localStorage.setItem('skin_color', skin_color.value)
  $router.push({ path: `/rooms/new` })
}
</script>
